PMarrayData.PositioningScript:
//format - var XYZ hould contain a string with x1:y1:z1:x2:y2:z2:...
//z is ignored for auto-z array type
var XYZ = '';
var R = 93.0;
var V3 = Math.sqrt(3.);
var Pi = 3.1415926
var Rmax = 728;
var Rho = 656;
var RhoA = 574;
var Rho1 = 650;
var Rho2 = 630;


for (var iy = -9; iy <=9; iy++)
  for (var ix = -9; ix <=9; ix++) {
    var x = R*(ix+0.5*iy);
    var y = R*V3*iy*0.5;
    var r = Math.sqrt(x*x+y*y);
    if ((r < 650)&&(Math.abs(r-620)>20)&&(Math.abs(r-580.8)>1))
    PM(x,y,0,0);
//    XYZ += x+ ':' + y +':'+0+':';
}

var Deg = Pi*2/360

for (var i=0; i<6; i++) {
  PM(Rho*Math.cos(Pi*2/48*(2+i*8)), Rho*Math.sin(Pi*2/48*(2+i*8)), 0, 0);
  PM(Rho*Math.cos(Pi*2/48*(6+i*8)), Rho*Math.sin(Pi*2/48*(6+i*8)), 0, 0);
  PM(RhoA*Math.cos(Pi*2/48*(2+i*8)+Deg), RhoA*Math.sin(Pi*2/48*(2+i*8)+Deg), 0, 0);
  PM(RhoA*Math.cos(Pi*2/48*(6+i*8)-Deg), RhoA*Math.sin(Pi*2/48*(6+i*8)-Deg), 0, 0);
}

for (var i=0; i<6; i++) {
  PM(Rho1*Math.cos(Pi*2/48*(0+i*8)), Rho1*Math.sin(Pi*2/48*(0+i*8)), 0, 0);
  PM(Rho1*Math.cos(Pi*2/48*(3+i*8)), Rho1*Math.sin(Pi*2/48*(3+i*8)), 0, 0);
  PM(Rho1*Math.cos(Pi*2/48*(5+i*8)), Rho1*Math.sin(Pi*2/48*(5+i*8)), 0, 0);
}
for (var i=0; i<6; i++) {
  PM(Rho2*Math.cos(Pi*2/48*(1+i*8)), Rho2*Math.sin(Pi*2/48*(1+i*8)), 0, 0);
  PM(Rho2*Math.cos(Pi*2/48*(7+i*8)), Rho2*Math.sin(Pi*2/48*(7+i*8)), 0, 0);
}


var dphi = Pi*2/96;

for (var ix = 0; ix <48; ix++)
    PM(Rmax*Math.cos(Pi*2/48*ix+dphi), Rmax*Math.sin(Pi*2/48*ix+dphi), 0, 0);

var R = 92;
var V3 = Math.sqrt(3.);
var Pi = 3.1415926
var Rmax = 728;
var Rho = 728;
var dphi = Pi*2/192;
var ddphi = 0; //Pi*2/384;


for (var iy = -9; iy <=9; iy++)
  for (var ix = -9; ix <=9; ix++) {
    var x = R*(ix+0.5*iy);
    var y = R*V3*iy*0.5;
    if (Math.sqrt(x*x+y*y) < 728)
    PM(x,y,0,0);
}

for (var i=0; i<6; i++) {
  PM(Rho*Math.cos(Pi*2/48*(0+i*8)), Rho*Math.sin(Pi*2/48*(0+i*8)), 0, 0);
  PM(Rho*Math.cos(Pi*2/48*(2.5+i*8)), Rho*Math.sin(Pi*2/48*(2.5+i*8)), 0, 0);
  PM(Rho*Math.cos(Pi*2/48*(5.5+i*8)), Rho*Math.sin(Pi*2/48*(5.5+i*8)), 0, 0);
//  XYZ += Rho*Math.cos(Pi*2/48*(7+i*8)+dphi-ddphi)+ ':' + Rho*Math.sin(Pi*2/48*(7+i*8)+dphi-ddphi) +':'+0+':';
}
